tinymce.PluginManager.add("reply_visible", function (editor, url) {
    var openDialog = function () {
        return editor.windowManager.open({
            title: "回复可见",
            body: {
                type: "panel",
                items: [
                    {
                        type: "textarea",
                        name: "detail",
                        label: "内容",
                    },
                ],
            },
            buttons: [
                {
                    type: "cancel",
                    text: "Close",
                },
                {
                    type: "submit",
                    text: "Save",
                    primary: true,
                },
            ],
            onSubmit: function (api) {
                var data = api.getData();
                // 将输入框内容插入到内容区光标位置
                editor.insertContent("[reply]" + data.detail + "[/reply]");
                api.close();
            },
        });
    };

    // 注册一个工具栏按钮名称
    editor.ui.registry.addButton("reply_visible", {
        text: "隐",
        tooltip: "发布回复可见内容",
        onAction: function () {
            openDialog();
        },
    });

    return {
        getMetadata: function () {
            return {
                //插件名和链接会显示在“帮助”→“插件”→“已安装的插件”中
                name: "reply_visible",
                url: "https://webape.net",
            };
        },
    };
});
